Search.SearchQuery:
SELECT Id as id, FirmName as firmName,ExpenseCategory as category, DATE_FORMAT(ExpenseDate,'%d/%m/%Y') as expenseDate,
                PaidTo as paidTo, Amount as amount, PaymentMode as paymentMode,IsApproved as isApproved, Status as status FROM
                ExpenseDetails WHERE {MACRO}

SELECT pd.PlotNo as plotNo, pd.FirmName as firmName,pd.ProjectName as projectName,pd.Block as block,pd.SqftRate as sqftRate, pd.PlotSize as plotSize,Facing as facing,cd.CustomerName as customerName,bd.TotalAmount as totalAmount, bd.TotalPaidAmount as totalPaidAmount FROM PlotDetails pd
                INNER JOIN PlotTransaction pt  ON pt.PlotId = pd.Id INNER JOIN  BookingDetails bd ON bd.BookingId = pt.BookingId INNER JOIN CustomerDetails cd ON  cd.BookingId = bd.BookingId
                WHERE {MACRO}

SELECT Id as plotId , PlotNo as plotNo, FirmName as firmName,PropertyTypeName as
                propertyTypeName,ProjectName,Block as block,SqftRate as sqftRate, PlotSize as plotSize, Facing as facing, HoldBy as holdBy, DATE_FORMAT(HoldOn,'%d/%m/%Y')  as holdOn FROM PlotDetails
                WHERE Status = 'Hold' AND {MACRO}

SELECT PlotNo as plotNo, FirmName as firmName,PropertyTypeName as
                propertyTypeName,ProjectName,Block as block,SqftRate as sqftRate, PlotSize as plotSize FROM PlotDetails
                WHERE {MACRO}

SELECT Id as id, FirmName as firmName,ExpenseCategory as category, DATE_FORMAT(ExpenseDate,'%d/%m/%Y') as expenseDate,
                PaidTo as paidTo, Amount as amount, TransactionDetail as transactionDetail, DATE_FORMAT(CreatedOn,'%d/%m/%Y') as createdOn, CreatedBy as createdBy, PaymentMode as paymentMode,IsApproved as isApproved FROM
                ExpenseDetails where {MACRO};
